@import "tailwindcss";

/* -------------------------------
   Custom Font: Gotham Bold
--------------------------------- */
@font-face {
  font-family: "Gotham Bold";
  src: url("/fonts/Gotham-Bold.woff2") format("woff2");
  font-weight: 800;
  font-style: normal;
  font-display: swap;
}

/* -------------------------------
   Root variables
--------------------------------- */
:root {
  --hero-font: "Gotham Bold", "Helvetica Neue", Arial, sans-serif;

  --background: #ffffff;
  --foreground: #171717;
}

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --font-sans: var(--font-geist-sans);
  --font-mono: var(--font-geist-mono);
}

/* Dark mode overrides */
@media (prefers-color-scheme: dark) {
  :root {
    --background: #0a0a0a;
    --foreground: #ededed;
  }
}

/* -------------------------------
   Base styles
--------------------------------- */
body {
  background: var(--background);
  color: var(--foreground);
  font-family: Arial, Helvetica, sans-serif;
  margin: 0;
  padding: 0;
}

